  };

  return (
    <div className="courses">
      <div className="container">
        {/* Header */}
        <div className="courses-header">
          <h1 className="courses-title">Explore Courses</h1>
          <p className="courses-description">
            Discover a wide range of educational content designed for rural students in Nabha
          </p>
        </div>

        {/* Filters */}
        <div className="courses-filters">
          <div className="search-box">
            <Search className="search-icon" />
            <input
              type="text"
              placeholder="Search courses..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
          </div>

          <div className="filter-group">
            <Filter className="filter-icon" />
            <select
              value={selectedSubject}
              onChange={(e) => setSelectedSubject(e.target.value)}
              className="filter-select"
            >
              {subjects.map(subject => (
                <option key={subject.value} value={subject.value}>
                  {subject.label}
                </option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <select
              value={selectedLevel}
              onChange={(e) => setSelectedLevel(e.target.value)}
              className="filter-select"
            >
              {levels.map(level => (
                <option key={level.value} value={level.value}>
                  {level.label}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Results Count */}
        <div className="results-info">
          <p>
            Showing {filteredCourses.length} of {courses.length} courses
          </p>
        </div>

        {/* Courses Grid */}
        <div className="courses-grid">
          {filteredCourses.map(course => (
            <div key={course.id} className="course-card">
              <div className="course-thumbnail">
                <div className="thumbnail-icon">{course.thumbnail}</div>
                <div className="course-badges">
                  {course.isOffline && (
                    <span className="badge offline-badge">
                      <Download className="badge-icon" />
                      Offline
                    </span>
                  )}
                  <span className="badge level-badge">
                    {course.level.charAt(0).toUpperCase() + course.level.slice(1)}
                  </span>
                </div>
              </div>

              <div className="course-content">
                <div className="course-header">
                  <h3 className="course-title">{course.title}</h3>
                  <div className="course-rating">
                    {renderStars(course.rating)}
                    <span className="rating-value">{course.rating}</span>
                  </div>
                </div>

                <p className="course-description">{course.description}</p>

                <div className="course-meta">
                  <div className="meta-item">
                    <Clock className="meta-icon" />
                    <span>{course.duration}</span>
                  </div>
                  <div className="meta-item">
                    <BookOpen className="meta-icon" />
                    <span>{course.lessons} lessons</span>
                  </div>
                  <div className="meta-item">
                    <Users className="meta-icon" />
                    <span>{course.students} students</span>
                  </div>
                </div>

                <div className="course-instructor">
                  <span className="instructor-label">Instructor:</span>
                  <span className="instructor-name">{course.instructor}</span>
                </div>

                <div className="course-actions">
                  <Link 
                    to={`/course/${course.id}`} 
                    className="btn btn-primary course-btn"
                  >
                    <Play className="btn-icon" />
                    Start Course
                  </Link>
                  {course.isOffline && (
                    <button 
                      className="btn btn-secondary course-btn"
                      onClick={() => handleDownload(course)}
                    >
                      <Download className="btn-icon" />
                      Download
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* No Results */}
        {filteredCourses.length === 0 && (
          <div className="no-results">
            <BookOpen className="no-results-icon" />
            <h3>No courses found</h3>
            <p>Try adjusting your search criteria or browse all courses.</p>
            <button 
              className="btn btn-primary"
              onClick={() => {
                setSearchTerm('');
                setSelectedSubject('all');
                setSelectedLevel('all');
              }}
            >
              Clear Filters
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Courses;
